import { Props, Key, Ref, ReactElementType } from 'shared/ReactTypes';
import { FunctionComponent, HostComponent, WorkTag } from './workTags';
import { Flags, NoFlags } from './filberFlags';
import { Container } from 'hostConfig';

/**
 * @tag 表示FiberNode是什么类型的节点
 */
export class FiberNode {
	type: any;
	tag: WorkTag;
	pendingProps: Props;
	key: Key;
	stateNode: any;
	ref: Ref;

	return: FiberNode | null;
	sibling: FiberNode | null;
	child: FiberNode | null;
	index: number;

	memoizedProps: Props | null;
	memoizedState: any;
	// 用于FiberNode和另外一个FiberNode之间切换
	alternate: FiberNode | null;
	// 保存操作标记
	flags: Flags;
	// 代表子树中包含的flags
	subtreeFlags: Flags;
	updateQueue: unknown;

	constructor(tag: WorkTag, pendingProps: Props, key: Key) {
		// 实例
		this.tag = tag;
		this.key = key;
		// stateNode比如对于HostComponent来说 <div> div DOM
		this.stateNode = null;
		// 对于FunctionComponent来说 type是 () => {}
		this.type = null;

		// 除了作为一个实例还有一个字段来表示节点关系
		// 构成树状结构
		// 指向父fiberNode
		this.return = null;
		// 指向兄弟fiberNode
		this.sibling = null;
		// 指向子fiberNode
		this.child = null;
		// <ul><li></li></ul>
		this.index = 0;
		this.ref = null;

		// 作为工作单元
		// 刚开始工作的时候的props是什么
		this.pendingProps = pendingProps;
		// 工作完之后的props是什么,也就是确定下来的props是什么
		this.memoizedProps = null;
		this.memoizedState = null;
		this.alternate = null;
		this.updateQueue = null;
		// 副作用
		this.flags = NoFlags;
		this.subtreeFlags = NoFlags;
	}
}

// 实现根节点fiberRootNode
export class fiberRootNode {
	// 宿主环境的dom挂载的节点
	// 但是我们不能直接定义为rootElement，因为对于其他环境就不是Element,我们需要一个更加抽象的名字
	container: Container;
	// 这个指向hostRootFiber
	current: FiberNode;
	// 这个指向我们更新完成以后的hostRootFiber
	finishedWork: FiberNode | null;
	constructor(container: Container, hostRootFiber: FiberNode) {
		this.container = container;
		this.current = hostRootFiber;
		hostRootFiber.stateNode = this;
		this.finishedWork = null;
	}
}

// current也就是hostRootFiber
// 这里用到了双缓存技术，
export const createWorkInProgress = (current: FiberNode, pendingProps: Props): FiberNode => {
	let wip = current.alternate;

	if (wip === null) {
		// 对于首屏渲染wip就是null
		// mount
		wip = new FiberNode(current.tag, pendingProps, current.key);
		wip.type = current.type;
		wip.stateNode = current.stateNode;

		wip.alternate = current;
		current.alternate = wip;
	} else {
		// update
		wip.pendingProps = pendingProps;
		// 将副作用东西全部清除
		wip.flags = NoFlags;
		wip.subtreeFlags = NoFlags;
	}
	wip.type = current.type;
	wip.updateQueue = current.updateQueue;
	wip.child = current.child;
	wip.memoizedProps = current.memoizedProps;
	wip.memoizedState = current.memoizedState;

	return wip;
};

// 根据element创建fiber
export function createFiberFromElement(element: ReactElementType): FiberNode {
	const { type, key, props } = element;
	// 根据不同的type来返回不同的FiberNode
	let fiberTag: WorkTag = FunctionComponent;
	if (typeof type === 'string') {
		// <div> type 'div'
		fiberTag = HostComponent;
	} else if (typeof type !== 'function' && __DEV__) {
		console.warn('未定义的type类型', element);
	}
	const fiber = new FiberNode(fiberTag, props, key);
	fiber.type = type;
	return fiber;
}
